INSERT INTO `templates` VALUES (590, NULL, 1, NULL, 'QueryDTO.java.btl', '2', 'generator-plus-dos/src/main/java/top/buukle/opensource/generator/plus/dtvo/dto', '1', 'top.buukle.opensource.generator.plus.dtvo.dto', NULL, '', 'package ${packageInfo};\n\nimport io.swagger.annotations.ApiModel;\nimport io.swagger.annotations.ApiModelProperty;\nimport lombok.Data;\nimport java.util.Date;\n\n/**\n * <p>\n * ${table.comment!}\n * </p>\n *\n * @author ${author}\n * @since ${date}\n */\n@Data\n@ApiModel(value = \" ${table.comment!} - 查询请求\")\npublic class ${entity}QueryDTO{\n\n    /**\n     * 当前页码\n     */\n    @ApiModelProperty(value = \"当前页码\")\n    private Integer pageNo = 1;\n    /**\n     * 页长\n     */\n    @ApiModelProperty(value = \"页长\")\n    private Integer pageSize = 10;\n    /**\n     * 起始时间\n     */\n    @ApiModelProperty(value = \"起始时间\")\n    private String startTime;\n    /**\n     * 截止时间\n     */\n    @ApiModelProperty(value = \"截止时间\")\n    private String endTime;\n\n    \n    <% for(field in table.fields){ %>\n    /**\n     * ${field.comment}\n     */\n    @ApiModelProperty(value = \"${field.comment}\")\n    private ${field.propertyType} ${field.propertyName};\n    <% } %>\n\n}', NULL, '', '2021-09-06 03:34:16', NULL, -1, NULL, '2021-11-29 16:30:36', 'ANONYMOUS', 'ANONYMOUS', NULL, 3, NULL, NULL, NULL, NULL, NULL);
