DROP TABLE IF EXISTS `templates_group`;
CREATE TABLE `templates_group`  (
  `id` int(16) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `audit_id` int(16) NULL DEFAULT NULL COMMENT '审批记录id',
  `application_code` varchar(64) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL,
  `name` varchar(128) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '模板分组名称',
  `params` varchar(128) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '模板分组自定义变量',
  `description` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '描述',
  `remark` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注',
  `gmt_created` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `creator` varchar(128) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL,
  `creator_tenant_id` int(16) NULL DEFAULT NULL COMMENT '创建者部门id',
  `creator_code` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '创建者UID',
  `gmt_modified` datetime NULL DEFAULT NULL COMMENT '更新时间',
  `modifier` varchar(128) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL,
  `modifier_code` varchar(64) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '更新者UID',
  `audit_status` int(1) NULL DEFAULT NULL COMMENT '审核状态  -1:待提交 0:提交审核  1:审核中 2: 审核不通过 3:审核通过',
  `status` int(1) NULL DEFAULT NULL COMMENT ' 状态 -1:已删除 0:创建完成 1:审核中 2: 停用 3: 正常',
  `bak01` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `bak02` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `bak03` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `bak04` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `bak05` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_creator_tenant_id`(`creator_tenant_id`) USING BTREE,
  INDEX `idx_status`(`status`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 607 CHARACTER SET = utf8 COLLATE = utf8_bin COMMENT = '模板分组' ROW_FORMAT = DYNAMIC;

